import React from "react";


class AddHabitForm extends React.Component {
    habitRef = React.createRef();
    timeFrameNumberRef = React.createRef();
    timeFrameLengthRef = React.createRef();
    habitCategoryRef = React.createRef();



    render() {
        return (
            <form className="habit-edit" onSubmit={this.createHabit}>
                <input name="habit" ref={this.habitRef} type="text" placeholder="Habit"/>
                <input name="timeFrameNumber" ref={this.timeFrameNumberRef} type="number" placeholder="Number of Times"/>
                <select name="timeFrameLength">
                    <option value="day">Day</option>
                    <option value="week">Week</option>
                    <option value="month">Month</option>
                    <option value="year">Year</option>
                </select>
                <select name="habitCategory" ref={this.timeFrameLengthRef}>
                    <option>Mind</option>
                    <option>Body</option>
                    <option>Soul</option>
                    <option>Professional</option>
                    <option>Relationship</option>
                </select>
                <button type="submit" ref={this.habitCategoryRef}>Add New Habit</button>
            </form>
        )

    }
}

export default AddHabitForm;
