import React from "react";
import HabitList from "./HabitList";
import Habit from "./Habit";
import sampleHabits from "../sample-habits";
import base from "../base";

class App extends React.Component {
  state = {
    habits: {}
  };

  componentDidMount() {
    const { params } = this.props.match;
    // first reinstate our localStorage
    const localStorageRef = localStorage.getItem(params.storeId);
    if (localStorageRef) {
      this.setState({ order: JSON.parse(localStorageRef) });
    }

    this.ref = base.syncState(`${params.storeId}/habits`, {
      context: this,
      state: "fishes"
    });
  }

  componentDidUpdate() {
    console.log(this.state.order);
    localStorage.setItem(
      this.props.match.params.storeId,
      JSON.stringify(this.state.order)
    );
  }

  componentWillUnmount() {
    base.removeBinding(this.ref);
  }


  addHabit = habit => {
    // 1. Take a copy of the existing state
    const habits = { ...this.state.habits };
    // 2. Add our new habit to that habits variable
    habits[`habit${Date.now()}`] = habit;
    // 3. Set the new fishes object to state
    this.setState({ habits });
  };

  loadSampleHabits = () => {
    this.setState({ habits: sampleHabits });
  };

  render() {
    return (
      <div >
        <HabitList  addHabit={this.addHabit} loadSampleHabits={this.loadSampleHabits} habits={this.state.habits}/>
        <ul className="habits">
          {Object.keys(this.state.habits).map(key => (
            <Habit
              key={key}
              index={key}
              details={this.state.habits[key]}
            />
          ))}
        </ul>
      </div>
    );
  }
}

export default App;
